import { Component, OnInit, Input } from '@angular/core';
import { FormGroup } from '@angular/forms';


@Component({
  selector: 'app-login-input',
  templateUrl: './input.component.html',
  styleUrls: ['./input.component.css']
})

export class LoginInputComponent implements OnInit {

  @Input() label: string
  @Input() name: string
  @Input() type: string
  @Input() input: boolean
  @Input() model: string
  @Input() form: FormGroup
  @Input() placeholder: string

  constructor() { }

  ngOnInit() {
  }

}
<div class="wrap" [formGroup]="this.form">
  <label [for]="this.name" class="label">{{label}}</label>
  <input [id]="this.name" [type]="this.type" [name]="this.name" required [formControlName]="this.name"
   class="input" [placeholder]="this.placeholder">
</div>
